// src/components/Home.js
import React from 'react';
import { Link } from 'react-router-dom';

const Home = () => {
  return (
    <div className="min-h-screen bg-gradient-to-br from-blue-900 via-blue-700 to-blue-500 flex flex-col items-center justify-center p-6 relative overflow-hidden">
      {/* Floating Decorative Elements */}
      <div className="absolute inset-0 overflow-hidden">
        <div className="absolute top-10 left-20 w-96 h-96 bg-blue-300 opacity-30 rounded-full filter blur-3xl animate-float"></div>
        <div className="absolute bottom-20 right-20 w-72 h-72 bg-blue-600 opacity-20 rounded-full filter blur-2xl animate-float-slow"></div>
        <div className="absolute bottom-40 left-40 w-80 h-80 bg-blue-400 opacity-25 rounded-full filter blur-3xl animate-float-reverse"></div>
        <div className="absolute top-1/4 right-1/3 w-60 h-60 bg-blue-200 opacity-20 rounded-full filter blur-2xl animate-float"></div>
      </div>

      {/* Welcome Section */}
      <div className="relative z-10 bg-white/90 backdrop-blur-lg p-12 rounded-3xl shadow-2xl text-center max-w-xl mx-auto transform transition-transform duration-500 hover:scale-105 hover:shadow-xl">
        <h1 className="text-5xl font-extrabold text-blue-800 mb-6 tracking-tight leading-tight">
          Welcome to Our <br /> International Customer Portal
        </h1>
        <p className="text-lg text-gray-700 mb-8 leading-relaxed">
          Your secure gateway to managing international payments and financial insights.
        </p>
        <Link
          to="/login"
          className="inline-block bg-gradient-to-r from-blue-500 to-blue-700 text-white text-lg font-semibold py-3 px-10 rounded-full shadow-lg transform transition-transform duration-300 hover:scale-110 hover:shadow-xl"
        >
          Login to Your Account
        </Link>
      </div>

      {/* Custom Animations */}
      <style jsx>{`
        @keyframes float {
          0%, 100% { transform: translateY(-10px); }
          50% { transform: translateY(10px); }
        }
        @keyframes float-slow {
          0%, 100% { transform: translateY(5px); }
          50% { transform: translateY(-5px); }
        }
        @keyframes float-reverse {
          0%, 100% { transform: translateY(10px); }
          50% { transform: translateY(-10px); }
        }
        .animate-float { animation: float 8s ease-in-out infinite; }
        .animate-float-slow { animation: float-slow 12s ease-in-out infinite; }
        .animate-float-reverse { animation: float-reverse 10s ease-in-out infinite; }
      `}</style>
    </div>
  );
};

export default Home;
